import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { targetAPI, scanAPI, vulnerabilityAPI, scannerAPI } from '../services/api';
import ScanForm from '../components/ScanForm';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    totalTargets: 0,
    totalScans: 0,
    activeScans: 0,
    totalVulnerabilities: 0,
    criticalVulns: 0,
    highVulns: 0,
  });
  const [recentScans, setRecentScans] = useState([]);
  const [scanners, setScanners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showScanForm, setShowScanForm] = useState(false);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      // Load targets
      const targets = await targetAPI.getAll(0, 1000);
      
      // Load scans
      const scans = await scanAPI.getAll(0, 1000);
      
      // Load vulnerabilities
      const vulnerabilities = await vulnerabilityAPI.getAll();
      
      // Load scanners
      const scannersData = await scannerAPI.getAvailable();

      // Calculate stats
      const activeScans = scans.filter(s => s.status === 'running').length;
      const criticalVulns = vulnerabilities.filter(v => v.severity === 'critical').length;
      const highVulns = vulnerabilities.filter(v => v.severity === 'high').length;

      setStats({
        totalTargets: targets.length,
        totalScans: scans.length,
        activeScans,
        totalVulnerabilities: vulnerabilities.length,
        criticalVulns,
        highVulns,
      });

      // Get 5 most recent scans
      const sortedScans = scans.sort((a, b) => 
        new Date(b.started_at) - new Date(a.started_at)
      ).slice(0, 5);
      setRecentScans(sortedScans);

      setScanners(scannersData.scanners || []);
    } catch (error) {
      console.error('Failed to load dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const getStatusBadgeClass = (status) => {
    const statusMap = {
      pending: 'bg-secondary',
      running: 'bg-primary',
      completed: 'bg-success',
      failed: 'bg-danger',
    };
    return statusMap[status] || 'bg-secondary';
  };

  const getSeverityBadgeClass = (severity) => {
    const severityMap = {
      critical: 'bg-danger',
      high: 'bg-warning text-dark',
      medium: 'bg-info text-dark',
      low: 'bg-success',
      info: 'bg-secondary',
    };
    return severityMap[severity] || 'bg-secondary';
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="spinner-container">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Dashboard</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowScanForm(true)}
        >
          <i className="bi bi-plus-circle me-2"></i>
          New Scan
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="row mb-4">
        <div className="col-md-3 mb-3">
          <div className="card stat-card">
            <div className="card-body">
              <div className="stat-icon bg-primary">
                <i className="bi bi-bullseye"></i>
              </div>
              <h3 className="stat-number">{stats.totalTargets}</h3>
              <p className="stat-label">Total Targets</p>
              <Link to="/targets" className="stat-link">View All →</Link>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card stat-card">
            <div className="card-body">
              <div className="stat-icon bg-info">
                <i className="bi bi-search"></i>
              </div>
              <h3 className="stat-number">{stats.totalScans}</h3>
              <p className="stat-label">Total Scans</p>
              <Link to="/scans" className="stat-link">View All →</Link>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card stat-card">
            <div className="card-body">
              <div className="stat-icon bg-success">
                <i className="bi bi-activity"></i>
              </div>
              <h3 className="stat-number">{stats.activeScans}</h3>
              <p className="stat-label">Active Scans</p>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card stat-card">
            <div className="card-body">
              <div className="stat-icon bg-danger">
                <i className="bi bi-exclamation-triangle"></i>
              </div>
              <h3 className="stat-number">{stats.totalVulnerabilities}</h3>
              <p className="stat-label">Vulnerabilities</p>
              <div className="stat-detail">
                <span className="badge bg-danger me-1">{stats.criticalVulns} Critical</span>
                <span className="badge bg-warning text-dark">{stats.highVulns} High</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Recent Scans */}
        <div className="col-md-8 mb-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Recent Scans</h5>
            </div>
            <div className="card-body">
              {recentScans.length === 0 ? (
                <p className="text-muted text-center py-3">No scans yet. Start your first scan!</p>
              ) : (
                <div className="table-responsive">
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Type</th>
                        <th>Status</th>
                        <th>Started</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentScans.map((scan) => (
                        <tr key={scan.id}>
                          <td>#{scan.id}</td>
                          <td>
                            <span className="badge bg-secondary">{scan.scan_type}</span>
                          </td>
                          <td>
                            <span className={`badge ${getStatusBadgeClass(scan.status)}`}>
                              {scan.status}
                            </span>
                          </td>
                          <td>{formatDate(scan.started_at)}</td>
                          <td>
                            <Link 
                              to={`/scans/${scan.id}`}
                              className="btn btn-sm btn-outline-primary"
                            >
                              View
                            </Link>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Scanner Status */}
        <div className="col-md-4 mb-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Scanner Status</h5>
            </div>
            <div className="card-body">
              {scanners.length === 0 ? (
                <p className="text-muted text-center py-3">Loading scanners...</p>
              ) : (
                <ul className="list-group list-group-flush">
                  {scanners.map((scanner) => (
                    <li key={scanner.name} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>{scanner.name}</strong>
                        <br />
                        <small className="text-muted">{scanner.description}</small>
                      </div>
                      <span className={`badge ${scanner.available ? 'bg-success' : 'bg-danger'}`}>
                        {scanner.available ? 'Available' : 'Unavailable'}
                      </span>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Scan Form Modal */}
      {showScanForm && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Start New Scan</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowScanForm(false)}
                ></button>
              </div>
              <div className="modal-body">
                <ScanForm 
                  onSuccess={() => {
                    setShowScanForm(false);
                    loadDashboardData();
                  }}
                  onCancel={() => setShowScanForm(false)}
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
